import React from 'react'

class DogInfo extends React.Component {

  render() {
    return(
      <>
        <img src={dog.image} alt={dog.name}/>
        <h2>{dog.name}</h2>
        {
          dog === "" ? null :    
          <button onClick={toggleisGoodDog}>
            {dog.isGoodDog ? "Good Dog!" : "Bad Dog!"}
          </button>
        }
      </>

    )
  }

}
